and news.addtime >= #{startDate}
and news.addtime <= #{endDate}
and (news.title like "%"#{key}"%" or news.detail like "%"#{key}"%")
ORDER BY news.addtime desc
ORDER BY news.addtime asc
limit #{start}, #{limit}

